function predictImage() {
//    console.log('Processing...');

    const IMG_CORE_SIZE = 20;
    const IMG_FULL_SIZE = 28;

    let image = cv.imread(canvas);
    // convert to B&W, single colour channel (actually RGB at the moment)
    // Colorspaces in OpenCV
    cv.cvtColor(image, image, cv.COLOR_RGBA2GRAY, 0);  // overwriting image
    // Make sure it is all stark white, makes contouring work better
    // Image thresholding in OpenCV
    cv.threshold(image, image, 63, 255, cv.THRESH_BINARY);  // <63 -> 0, > 63 -> 255
    // Need to find contours in order to then get bounding rectangle
    // Contours in OpenCV
    contours = new cv.MatVector();
    hierarchy = new cv.Mat();
    // nb would probably be better off with RETR_EXTERNAL, see docs https://docs.opencv.org/3.4/d9/d8b/tutorial_py_contours_hierarchy.html
    cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE)
    // Get bounding rectangle of the drawing and clip the image
    // Contour features in OpenCV
    let cnt = contours.get(0);
    let rect = cv.boundingRect(cnt);
    image = image.roi(rect);

    // Geometric Transformations of Images on OpenCV
    // https://docs.opencv.org/master/dd/d52/tutorial_js_geometric_transformations.html
    var height = image.rows;
    var width = image.cols;
    var scaleFactor = Math.min(IMG_CORE_SIZE/height, IMG_CORE_SIZE/width);
    let dsize = new cv.Size(Math.round(width*scaleFactor), Math.round(height*scaleFactor));
    //cv.resize(image, image, cv.Size(), scaleFactor, scaleFactor, cv.INTER_AREA);
    cv.resize(image, image, dsize, 0, 0, cv.INTER_AREA);
    // add padding to make image square again, and up to full size
    //   main character is now in centre of image and slightly smaller, same as MNIST images
    //  Core operations in OpenCV
    height = image.rows;  // new sizes
    width = image.cols;
    // ceil and floor to cope with half values
    const LEFT = Math.ceil((IMG_FULL_SIZE-width)/2);
    const RIGHT = Math.floor((IMG_FULL_SIZE-width)/2);
    const TOP = Math.ceil((IMG_FULL_SIZE-height)/2);
    const BOTTOM = Math.floor((IMG_FULL_SIZE-height)/2);
//    console.log(`top: ${TOP}, bottom: ${BOTTOM}, left: ${LEFT}, right: ${RIGHT}`);
    let s = new cv.Scalar(0, 0, 0, 255);  // black, fully opaque
    cv.copyMakeBorder(image, image, TOP, BOTTOM, LEFT, RIGHT, cv.BORDER_CONSTANT, s);

    // Centre of mass
    // Contour features (moments) in OpenCV
    cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);
    cnt = contours.get(0);
    const Moments = cv.moments(cnt, false);

    const cx = Moments.m10/Moments.m00;
    const cy = Moments.m01/Moments.m00;
//    console.log(`M00 = ${Moments.m00}, cx: ${cx}, cy: ${cy}`);

    //  Shift so that centre of mass is in centre of image
    // Geometric transformations of images in OpenCV
    // Affine transformation
    const X_SHIFT = Math.round(IMG_FULL_SIZE/2.0 - cx);
    const Y_SHIFT = Math.round(IMG_FULL_SIZE/2.0 - cy);
    let M = cv.matFromArray(2, 3, cv.CV_64FC1, [1, 0, X_SHIFT, 0, 1, Y_SHIFT]);
    dsize = new cv.Size(image.cols, image.rows);
    cv.warpAffine(image, image, M, dsize, cv.INTER_LINEAR, cv.BORDER_WRAP);

    let pixelValues = image.data;
//    console.log(`pixel values: ${pixelValues}`);
    // Normalise the pixel values for the machine learning algorithm
    pixelValues = Float32Array.from(pixelValues);
    pixelValues = pixelValues.map(function(item){
        return item / 255.0;
    });
//    console.log(`scaled pixel values: ${pixelValues}`);


    const X = tf.tensor([pixelValues]);  // add extra pair of [] to make it 2-D
//    console.log(`Shape of Tensor: ${X.shape}`);
//    console.log(`dtype of Tensor: ${X.dtype}`);
    const answer = model.predict(X);
    answer.print();

//    console.log(tf.memory());

    const output = answer.dataSync()[0];  // answer is a Tensor - use dataSync to get number out

    // for Testing only
//    const outputCanvas = document.createElement('CANVAS');
//    cv.imshow(outputCanvas, image);
//    document.body.appendChild(outputCanvas);
 
    // Cleanup
    image.delete();
    contours.delete();
    hierarchy.delete();
    cnt.delete();
    M.delete();

    X.dispose();
    answer.dispose();

//    console.log(output);
    return output;

}